const currencies = {
  'da-DK': 'DKK',
  'de-DE': 'EUR',
  'en-US': 'USD',
  'en-GB': 'GBP',
  'es-ES': 'EUR',
  'fi-FI': 'EUR',
  'fr-FR': 'EUR',
  'it-IT': 'EUR',
  'ja-JP': 'JPY',
  'nb-NO': 'NOK',
  'nl-NL': 'EUR',
  'pt-BR': 'BRL',
  'sv-SE': 'SEK',
  'zh-CN': 'CNY',
  'zh-TW': 'TWD',
  'ko-KR': 'KRW',
  'cs-CZ': 'CZK',
  'pl-PL': 'PLN',
  'ru-RU': 'RUB',
  'tr-TR': 'TRY'
};
const locales = Object.keys(currencies);
const getCurrency = function (locale) {
  if (locales.indexOf(locale) > -1) {
    return currencies[locale]
  } else {
    const matchingLocale = locales.find(x => x.startsWith(locale));
    if (matchingLocale) {
      return currencies[matchingLocale]
    }
  }
  return ''
};

export { getCurrency };
